INSERT INTO `db_internships` VALUES ('0041', 'Software Engineer Intern', '234115225', NULL, '1', '2022-07-25 15:28:06', '2022-07-25 15:28:08', 0, 1, NULL, 'Singapore', 'Science', 'Singapore', 'Singapore', 'Gain first-hand experience working on Shopee’s leading platform to enhance the user experience. Have the opportunity to work alongside top engineers to gain invaluable insights on how to build a scalable and high-performance software platform.\r\n\r\n', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0042', 'Intern - NPI and Facility Design', '234115225', NULL, '1', '2022-07-25 15:28:37', '2022-07-25 15:28:39', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'The intern will be provided a rare opportunity to gain exposure to technology transfer of a new 2nd Generation product involving enzymatic chemistry and a complex capital project involving the transformation of an existing manufacturing facility to support the new product.\r\n', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0043', 'Intern - Quality Improvement', '234115225', NULL, '1', '2022-07-25 15:29:25', '2022-07-25 15:29:28', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'Data Integrity 0 engagement of cross functions as part of information gathering and project implementation for site wide Data integrity programme . This mimics a role of quality officer, giving the candidate a hands-on and experience in the capacity of the quality officer.', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0044', 'Intern - Technology Optimization', '234115225', NULL, '1', '2022-07-25 15:30:05', '2022-07-25 15:30:07', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'Support the design, process safety assessments, commissioning and validation on manufacture of pharmaceutical intermediates and final active pharmaceutical ingredients (API).\r\n\r\n', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0045', 'Intern - Production Improvement', '234115225', NULL, '1', '2022-07-25 15:30:30', '2022-07-25 15:30:36', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'Monitoring of Distillation Column critical processing parameters using heat and mass balance; Review of batch records / log sheets. Involvement in troubleshooting, continuous improvement projects and investigations.\r\n\r\n', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0046', 'Intern - Process Automation', '234115225', NULL, '1', '2022-07-25 15:31:00', '2022-07-25 15:31:04', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'This project focuses on understanding the solvent recovery process that is operated via DeltaV DCS in a remote manual mode and work closely with automation engineer to build sequence in DeltaV DCS to automate the recovery process.\r\n\r\n', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0047', 'Intern - Value Stream 2 - NPI', '234115225', NULL, '1', '2022-07-25 15:31:42', '2022-07-25 15:31:45', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'The scope of the internship will mimic the role of a production engineer leading operational improvement projects through the use of Lean Six Sigma tools, Digital Data & Analytics, and platforms such as Power BI, Power Apps & Microsoft Excel.', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0048', 'Applied Data Intern', '234115225', NULL, '1', '2022-07-25 15:32:07', '2022-07-25 15:32:09', 0, 1, NULL, 'Singapore', 'Information Technology', 'Singapore', 'Singapore', 'The intern will work under the supervision of the External Quality (EQ) Systems team. The intern will collaborate with the regional EQ teams for insights on business decisions.', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
INSERT INTO `db_internships` VALUES ('0049', 'Intern - Data Integrity', '234115225', NULL, '1', '2022-07-25 15:33:10', '2022-07-25 15:33:13', 0, 1, NULL, 'Singapore', 'Engineering', 'Singapore', 'Singapore', 'Intern will be required to perform data gathering & analysis using process maps of end-to-end data flow. Intern should demonstrate systems thinking. In addition, to also support cutover/upgrade of enterprise electronic documentation management system.', 1, 'Please send your resume to 1184344283@qq.com', 'HR Island');
